={{
'ðŸ—“ï¸ ' + DateTime.now().setZone('Asia/Dhaka').toFormat("yyyy-MM-dd hh:mm a") + '\n\n' +

$items()
  .filter(item => !item.json["Expense Category"])
  .map(item => {
    const t = item.json;
    const cat = t.Category || '';
    const sub = t.Subcategory || '';
    const amount = t.Amount?.toFixed(2) || '0.00';
    const desc = t["Desc."] || '';

    return `â€¢ ${cat}${sub ? ' > ' + sub : ''}  
  Amount: à§³${amount}  
  ${desc ? 'Note: ' + desc : ''}`;
  })
  .join('\n\n') +

'\n\n' +

$items()
  .filter(item => item.json["Expense Category"])
  .map(item => {
    const t = item.json;
    const label = t["Expense Category"];
    const value = t.Total?.toFixed(2) || '0.00';
    return `**${label}**: à§³${value}`;
  })
  .join('\n')
}}

